package kz.jazzsoft.bnd.event.adapter;import java.io.IOException;import java.io.StringWriter;import java.io.UnsupportedEncodingException;import org.codehaus.jackson.map.ObjectMapper;/** * Created by: pavel.kurdyukov * Created: 14.01.13 16:44 * Copyright Â© LLP JazzSoft */public class Util {    public <T> T fromJSON(String json, Class<T> type) {        ObjectMapper objectMapper = new ObjectMapper();        try {            return objectMapper.readValue(json, type);        } catch (IOException e) {            e.printStackTrace();        }        return null;    }    public String toJSON(Object o) {        ObjectMapper objectMapper = new ObjectMapper();        StringWriter stringWriter = new StringWriter();        try {            objectMapper.writeValue(stringWriter, o);            return stringWriter.toString();        } catch (IOException e) {            e.printStackTrace();        }        return null;    }    public String bytesToString(byte[] data) {        try {            return new String(data, "UTF-8");        } catch (UnsupportedEncodingException e) {            e.printStackTrace();        }        return null;    }    public byte[] stringToBytes(String str) {        try {            return str.getBytes("UTF-8");        } catch (UnsupportedEncodingException e) {            e.printStackTrace();        }        return null;    }}