package kz.jazzsoft.bnd.event.dal;import java.io.IOException;import java.io.InputStream;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;public class SessionFactory {		public static SqlSessionFactory getSessionFactory() throws IOException{			String path = "mybatis/config.xml";			InputStream input = Resources.getResourceAsStream(path);			return (new SqlSessionFactoryBuilder().build(input));	}}