package kz.jazzsoft.bnd.event.ui.table;import com.vaadin.terminal.ThemeResource;import com.vaadin.ui.Alignment;import com.vaadin.ui.Button;import com.vaadin.ui.FormLayout;import com.vaadin.ui.HorizontalLayout;import com.vaadin.ui.Label;import com.vaadin.ui.TextArea;import com.vaadin.ui.TextField;import com.vaadin.ui.VerticalLayout;import com.vaadin.ui.Window;import com.vaadin.ui.themes.Reindeer;public class EventDialog extends Window{		private Object target;		TextField event_type_field ;	TextField component_name_field ;	TextField module_name_field ;	TextArea description_field ;	TextField user_field ;	TextField date_time_field ;	TextField resource_field ;		Button cancel, up, down;		VerticalLayout leftMainLayout, rightLayout, rightCenterLayout;	HorizontalLayout bottom, mainLayout;		public EventDialog() 	{		setWidth("480px");		setModal(true);		setResizable(false);				leftMainLayout = new VerticalLayout();		leftMainLayout.setSpacing(true);				bottom = new HorizontalLayout();		bottom.setWidth("100%");				mainLayout = new HorizontalLayout();				event_type_field = new TextField();		component_name_field = new TextField();		module_name_field = new TextField();		user_field = new TextField();		description_field = new TextArea();		date_time_field = new TextField();		resource_field = new TextField();				event_type_field.setColumns(15);		component_name_field.setColumns(15);		module_name_field.setColumns(15);		user_field.setColumns(15);		description_field.setColumns(15);		date_time_field.setColumns(15);		resource_field.setColumns(15);				cancel = new Button("Close");				rightLayout = new VerticalLayout();		rightLayout.setHeight("100%");		rightCenterLayout = new VerticalLayout();		rightCenterLayout.setSizeUndefined();		rightCenterLayout.setSpacing(true);				up = new Button();		up.setIcon(new ThemeResource("icon/bullet_arrow_up.png"));		up.setStyleName(Reindeer.BUTTON_LINK);		down = new Button();		down.setIcon(new ThemeResource("icon/bullet_arrow_down.png"));		down.setStyleName(Reindeer.BUTTON_LINK);			}		public void setTarget(Object target)	{		this.target = target;	}		public void getEvent()	{					FormLayout form = new FormLayout();			form.setMargin(true);			form.setSpacing(true);						form.addComponent(event_type_field);			form.addComponent(component_name_field);			form.addComponent(module_name_field);			form.addComponent(description_field);			form.addComponent(user_field);			form.addComponent(date_time_field);			form.addComponent(resource_field);			bottom.addComponent(cancel);			bottom.setMargin(false, true, false, false);			bottom.setComponentAlignment(cancel, Alignment.MIDDLE_RIGHT);						leftMainLayout.addComponent(form);			leftMainLayout.addComponent(bottom);									leftMainLayout.setComponentAlignment(bottom, Alignment.MIDDLE_RIGHT);			rightCenterLayout.addComponent(up);			rightCenterLayout.addComponent(down);						rightLayout.addComponent(rightCenterLayout);			rightLayout.setComponentAlignment(rightCenterLayout, Alignment.MIDDLE_CENTER);						mainLayout.addComponent(leftMainLayout);			mainLayout.addComponent(rightLayout);			mainLayout.setExpandRatio(leftMainLayout, 1.0f);			mainLayout.setMargin(true);						setContent(mainLayout);			center();			}		public void setButtonCaption(String cap)	{		cancel.setCaption(cap);	}		public Button getButton()	{		return cancel;	}		public void setEventType(String caption, Object value)	{		event_type_field.setCaption(caption);		event_type_field.setValue(value);	}		public void setComponentName(String caption, Object value)	{		component_name_field.setCaption(caption);		component_name_field.setValue(value);	}		public void setModuleName(String caption, Object value)	{		module_name_field.setCaption(caption);		module_name_field.setValue(value);	}		public void setDescription(String caption, Object value)	{		description_field.setCaption(caption);		description_field.setValue(value);	}		public void setUser(String caption, Object value)	{		user_field.setCaption(caption);		user_field.setValue(value);	}		public void setDateTime(String caption, Object value)	{		date_time_field.setCaption(caption);		date_time_field.setValue(value);	}		public void setResource(String caption, Object value)	{		resource_field.setCaption(caption);		resource_field.setValue(value);	}		public void setWindowCaption(String caption)	{		setCaption(caption);	}		public Button getUp()	{		return up;	}		public Button getDown()	{		return down;	}	}