package kz.jazzsoft.bnd.event.ui.table;import com.vaadin.terminal.ThemeResource;import com.vaadin.ui.Alignment;import com.vaadin.ui.Button;import com.vaadin.ui.ComboBox;import com.vaadin.ui.HorizontalLayout;import com.vaadin.ui.Label;import com.vaadin.ui.TextField;import com.vaadin.ui.themes.Reindeer;public class PageControlLayout extends HorizontalLayout{		ComboBox pagesCombo;	TextField input;	Button  next, previous, last, first;	Label page, page_amount ,pageItem;		   public PageControlLayout() {	   setWidth("100%");	   pagesCombo = new ComboBox();	   pagesCombo.setNullSelectionAllowed(false);	   pagesCombo.setImmediate(true);	   pagesCombo.setMultiSelect(false);	   pagesCombo.setTextInputAllowed(false);	   pagesCombo.setWidth("50px");	   pagesCombo.addItem("5");	   pagesCombo.addItem("15");	   pagesCombo.addItem("50");	   pagesCombo.addItem("100");	   pagesCombo.addItem("300");	   pagesCombo.addItem("500");	   	   	   input = new TextField();	   input.setImmediate(true);	   input.setColumns(3);	   next = new Button();	   previous = new Button();	   last = new Button();	   first = new Button();	   	   page_amount = new Label("1", Label.CONTENT_XHTML);	   page = new Label("Pages", Label.CONTENT_XHTML);	   pageItem = new Label("Items per page", Label.CONTENT_XHTML);	   	   first.setIcon(new ThemeResource("icon/resultset_first.png"));	   first.setStyleName(Reindeer.BUTTON_LINK);	   first.setSizeFull();	   previous.setIcon(new ThemeResource("icon/resultset_previous.png"));	   previous.setStyleName(Reindeer.BUTTON_LINK);	   previous.setSizeFull();	   next.setIcon(new ThemeResource("icon/resultset_next.png"));	   next.setStyleName(Reindeer.BUTTON_LINK);	   next.setSizeFull();	   last.setIcon(new ThemeResource("icon/resultset_last.png"));	   last.setStyleName(Reindeer.BUTTON_LINK);	   last.setSizeUndefined();   }      public HorizontalLayout pageControlPanel() {	   HorizontalLayout left = new HorizontalLayout();	   left.setSizeUndefined();	   left.addComponent(pageItem);  	   left.addComponent(pagesCombo);	   	   addComponent(left);	   setComponentAlignment(left, Alignment.TOP_LEFT);	   	   HorizontalLayout right = new HorizontalLayout();	   right.setSizeUndefined();	   right.addComponent(first);	   right.addComponent(previous);	   right.addComponent(page);	   right.addComponent(input);	   right.addComponent(page_amount);	   right.addComponent(next);	   right.addComponent(last);	   addComponent(right);	   setComponentAlignment(right, Alignment.TOP_CENTER);	   return this;   }      public Button getFirstLabel(){	   return first;   }   public Button getNextLabel(){	   return next;   }   public Button getPreviousLabel(){	   return previous;   }      public Button getLastLabel(){	   return last;   }   public Label getPageItem(){	   return pageItem;   }   public Label getPage() {	   return page;   }   public Label getPageCount(){	   return page_amount;   }   public TextField getInputPage(){	   return input;   }   public ComboBox getpagesCom(){	   return pagesCombo;   }      public void setPageItem(String value) {	   if(value==null)		   value="Item per page";	   pageItem.setValue(value+":&nbsp");   }   public void setPageCount(String value) {	   if(value==null)		   value="10";	   page_amount.setValue("&nbsp/&nbsp"+value);   }      public void setPage(String value) {	   if(value==null)		   value="Page";	   page.setValue(value+":&nbsp");   }   }