package kz.jazzsoft.bnd.event.ui.table.containers;import java.io.IOException;import java.util.Properties;import org.springframework.core.io.ClassPathResource;import org.springframework.stereotype.Component;/** * Config DB setting */@Componentpublic class Dbconfig {	  Properties properties;	    public Dbconfig() {	        try {	            properties = new Properties();	            properties.load(new ClassPathResource("spring/config.properties").getInputStream());	            getClass();	        } catch (IOException e) {	            e.printStackTrace();	        }	    }	    /**	     * Get DB user name	     *	     * @return db.username	     */	    public String getUserName() {	        return properties.getProperty("db.username");	    }	    /**	     * Get DB user password	     *	     * @return db.password	     */	    public String getUserPassword() {	        return properties.getProperty("db.password");	    }	    /**	     * Get DB URL	     *	     * @return db.url	     */	    public String getDBURL() {	       return properties.getProperty("db.url");	    }	    	    /**	     * Get DB archive URL	     *	     * @return db.archive.url	     */	    public String getDBArchiveURL() {	       return properties.getProperty("db.archive.url");	    }	    /**	     * Get DB name	     *	     * @return db.name	     */	    public String getDBName() {	       return properties.getProperty("db.name");	    }	    	    /**	     * Get Archive data base name	     *	     * @return db.archive.name	     */	    public String getDBArchiveName() {	       return properties.getProperty("db.archive.name");	    }	    	    /**	     * Get db IP	     *	     * @return db.ip	     */	    public String getDBIP() {	       return properties.getProperty("db.ip");	    }	    	    /**	     * Get port	     *	     * @return db.port	     */	    public String getDBPort() {	       return properties.getProperty("db.port");	    }	    	    /**	     * Get PostgreSQL pg_dump file directory	     *	     * @return pg_dump	     */	    public String getPG_dump() {	       return properties.getProperty("pg_dump");	    }	    	    /**	     * Get PostgreSQL pg_restore file directory	     *	     * @return pg_restore	     */	    public String getPG_restore() {	       return properties.getProperty("pg_restore");	    }}